mapper.select:
select count(0)
from t_goods g
where g.delete_status=0

    and g.grade_id = #{gradeId}


    and g.subject_id = #{subjectId}

select g.id id, g.goods_code goodsCode, g.goods_name goodsName, g.status status, g.goods_url goodUrl, g.goods_desc goodDesc, g.price price, g.sale_price salePrice, g.img img, s.name supplierName, gd.name gradeName, sj.name subjectName from t_goods g left join t_supplier s on s.id=g.supplier_id left join t_grade gd on gd.id=g.grade_id left join t_subject sj on sj.id=g.subject_id where g.delete_status=0
and g.grade_id = #{gradeId}
and g.subject_id = #{subjectId}
order by g.id limit #{offset},#{pageRow}
